import { useTransform } from './use-transform.js';
import { invariant, warning } from 'hey-listen';
import { useMotionValue } from './use-motion-value.js';
import { useVisualElementContext } from '../context/MotionContext/index.js'; // Keep things reasonable and avoid scale: Infinity. In practise we might need
// to add another value, opacity, that could interpolate scaleX/Y [0,0.01] => [0,1]
// to simply hide content at unreasonable scales.

var maxScale = 100000;

var invertScale = function (scale) {
  return scale > 0.001 ? 1 / scale : maxScale;
};

